import React, { useState, useEffect } from 'react';
import './About.css';

function About() {
  const [text, setText] = useState('');
  const content = "I'm Sukrit, a passionate front-end developer with expertise in crafting engaging user experiences using React.js. Through my journey, I've honed my skills to create seamless and interactive web applications. My proficiency in React allows me to build dynamic and responsive designs that capture the essence of modern web development. I take pride in my work and have successfully delivered projects that align with clients' visions. Notably, my dedication led to the completion of several impactful projects. Currently, I'm dedicated to refining my craft and exploring new challenges in the world of front-end development. Feel free to reach out to me at sukrit37@gmail.com if you're looking to collaborate on a project or enhance your online presence. I'm excited to bring innovative solutions to the table!";
  const typingSpeed = 50; // Adjust the typing speed (milliseconds per character)

  useEffect(() => {
    let index = 0;

    const typingInterval = setInterval(() => {
      if (index < content.length) {
        setText((prevText) => prevText + content.charAt(index));
        index++;
      } else {
        clearInterval(typingInterval);
      }
    }, typingSpeed);

    return () => clearInterval(typingInterval);
  }, []);

  return (
    <div className='about-body'>
      <p className='about-p'>I'm <b>Sukrit</b>, a passionate front-end developer with expertise in crafting engaging user experiences using React.js. Through my journey, 
      I've honed my skills to create seamless and interactive web applications. My proficiency in React allows me to build dynamic and responsive designs that capture the essence of modern web development. I take pride in my work and have successfully delivered projects that align with clients' visions. Notably,
       my dedication led to the completion of several impactful projects. Currently, I'm dedicated to refining my craft and exploring new challenges in the world of front-end development. 
       Feel free to reach out to me at <b>sukrit37@gmail.com</b> if you're looking to collaborate on a project or enhance your online presence. I'm excited to bring innovative solutions to the table!</p>
    </div>
  );
}

export default About;
